import * as React from "react";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import Link from "@mui/material/Link";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { Link as LinkR, useNavigate } from "react-router-dom";
import { register } from "../../JS/actions/userActions";
import { useDispatch } from "react-redux";
import NavBar from "../NavBar/NavBar"


function Copyright(props) {
  return (
    <Typography
      variant="body2"
      color="text.secondary"
      align="center"
      {...props}
    >
      {"Copyright © "}
      <Link color="inherit" href="https://mui.com/">
        Your Website
      </Link>{" "}
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}

const theme = createTheme();

export default function SignUp() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  
  const [email, setEmail] = React.useState("");
  const [password, setPassword] = React.useState("");
  const [fullName, setFullName] = React.useState("");
  const [role, setRole] = React.useState(["candidat"]);
  const [pic, setPic] = React.useState("")
  const handleChange = (e) =>{
    setRole(e.currentTarget.value)
  }


  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData();
    console.log({
      email: data.get("email"),
      password: data.get("password"),
      fullName: data.get("firstName") + " " + data.get("lastName"),
      dateNais: data.get("dateNais"),
      role: role,
      pic: data.get("pic"),
    });
    data.append("email",email)
    data.append("password"+ " " +"lastName",password)
    data.append("firstName",fullName)
    data.append("file",image)
    dispatch(
      register(
        {
          email: data.get("email"),
          password: data.get("password"),
          fullName: data.get("firstName") + " " + data.get("lastName"),
          dateNais: data.get("dateNais"),
          role: role,
          pic: data.get("pic"),
          
        },
        navigate
      )
    );
  };

  return (
    <div>
      <NavBar />
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign up
          </Typography>
          <Box
            component="form"
            noValidate
            onSubmit={handleSubmit}
            sx={{ mt: 3 }}
          >
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  autoComplete="given-name"
                  name="firstName"
                  required
                  fullWidth
                  id="firstName"
                  label="First Name"
                  autoFocus
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  id="lastName"
                  label="Last Name"
                  name="lastName"
                  autoComplete="family-name"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  name="email"
                  autoComplete="email"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="password"
                  label="Password"
                  type="password"
                  id="password"
                  autoComplete="new-password"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="dateNais"
                  
                  type="date"
                  id="dateNais"
                  autoComplete="new-date"
                />
              </Grid>
              <Grid item xs={12}>
                <select style={{width:400 , height:60}} value={role} onChange={handleChange} >
                  <option value="candidat">Candidat</option>
                  <option value="formateur">Formateur</option>
                </select>

                
              </Grid>
              <Grid item xs={12}>
              {(role ==="formateur") ? 
              
              <TextField
                required
                fullWidth
                name="pic"
                type="file"
                id="pic"
                autoComplete="file"
                onChange={(e)=>setPic(e.target.files[0])}
              />:<div></div>}
              </Grid>

              <Grid item xs={12}>
                <FormControlLabel
                  control={
                    <Checkbox value="allowExtraEmails" color="primary" />
                  }
                  label="I want to receive inspiration, marketing promotions and updates via email."
                />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Sign Up
            </Button>
            <Grid container justifyContent="flex-end">
              <Grid item>
                <LinkR to="/Login">Already have an account? Sign in</LinkR>
              </Grid>
            </Grid>
          </Box>
        </Box>
        <Copyright sx={{ mt: 5 }} />
      </Container>
    </ThemeProvider>
    </div>
  );
}
